export const AIAuditor = { scanDOM: () => { const proposals = []; const root = document.body; const textAreas = root.querySelectorAll('textarea'); textAreas.forEach(el => { const label = AIAuditor.findLabel(el); proposals.push({ type: 'Generative Text', element: label || el.id || 'Text Area', suggestion: 'Add AI Autocomplete or Summarization', confidence: 'High', impact: 'Efficiency' }); }); const lists = root.querySelectorAll('ul, table, .grid'); lists.forEach(el => { if (el.children.length > 10) { proposals.push({ type: 'Data Analysis', element: el.id || el.className || 'List/Table', suggestion: 'Add AI Smart Search & Filtering', confidence: 'Medium', impact: 'UX' }); } }); const fileInputs = root.querySelectorAll('input[type="file"]'); fileInputs.forEach(el => { proposals.push({ type: 'Computer Vision', element: el.id || 'File Upload', suggestion: 'Add AI OCR or Image Analysis', confidence: 'High', impact: 'Automation' }); }); const forms = root.querySelectorAll('form'); forms.forEach(el => { if (el.querySelectorAll('input, select').length > 5) { proposals.push({ type: 'Automation', element: el.id || 'Form', suggestion: 'Add AI Form Filling / Prediction', confidence: 'Medium', impact: 'Speed' }); } }); return proposals; }, findLabel: (el) => { if (el.id) { const label = document.querySelector(`label[for="${el.id}"]`); if (label) return label.innerText; } return null; } };