export const Admin = { settings: { enabled: true, tuningParams: { temperature: 0.7, persona: 'helpful_assistant' }, requiredTier: 'pro' }, init: () => { const saved = localStorage.getItem('ai_overlay_settings'); if (saved) { try { const parsed = JSON.parse(saved); Admin.settings = { ...Admin.settings, ...parsed }; console.log("[AI Admin] Settings loaded:", Admin.settings); } catch (e) { console.warn("[AI Admin] Failed to parse saved settings", e); } } }, shouldActivate: (user) => { if (!Admin.settings.enabled) return false; if (user && user.subscriptionTier) { } return true; }, updateTuning: (newParams) => { Admin.settings.tuningParams = { ...Admin.settings.tuningParams, ...newParams }; Admin.save(); console.log("[AI Admin] Tuning updated:", Admin.settings.tuningParams); }, toggle: (state) => { Admin.settings.enabled = state; Admin.save(); return Admin.settings.enabled; }, save: () => { localStorage.setItem('ai_overlay_settings', JSON.stringify(Admin.settings)); } };