export const SmartQueue = { queue: [], isProcessing: false, PRIORITY: { CRITICAL: 10, HIGH: 5, NORMAL: 1, LOW: 0 }, enqueue: (item) => { SmartQueue.queue.push(item); SmartQueue.queue.sort((a, b) => b.priority - a.priority || a.timestamp - b.timestamp); console.log(`[SmartQueue] Added item. Size: ${SmartQueue.queue.length}`); }, getNext: (userAvailable = true) => { if (!userAvailable || SmartQueue.queue.length === 0) return null; return SmartQueue.queue.shift(); }, peek: () => { return SmartQueue.queue.length > 0 ? SmartQueue.queue[0] : null; }, isEmpty: () => SmartQueue.queue.length === 0 };