export class BaseBridge { constructor(sourceModule, targetModule) { this.source = sourceModule; this.target = targetModule; } extract(context) { throw new Error("Method 'extract()' must be implemented."); } transform(rawData) { throw new Error("Method 'transform()' must be implemented."); } async load(processedData, destinationRef) { throw new Error("Method 'load()' must be implemented."); } async execute(context, destinationRef) { console.log(`[Bridge] Executing ${this.constructor.name}...`); try { const raw = await this.extract(context); const processed = this.transform(raw); const result = await this.load(processed, destinationRef); console.log(`[Bridge] ${this.constructor.name} completed successfully.`); return result; } catch (error) { console.error(`[Bridge] Error in ${this.constructor.name}:`, error); throw error; } } }