import { BaseBridge } from './BaseBridge.js'; import { calculateSnowServiceCost } from '../snow_calc/snow-logic-advanced.js'; export class PricingBridge extends BaseBridge { constructor() { super('Sketch', 'Estimate'); } extract(context) { const { drawings, calculatorManager } = context; const measurements = drawings.map(d => ({ id: d.id, type: d.measurementType, value: d.measurement, serviceId: d.service })); let tradeInputs = null; if (calculatorManager && calculatorManager.mode === 'snow') { tradeInputs = calculatorManager.data || {}; } return { measurements, tradeInputs, mode: calculatorManager ? calculatorManager.mode : 'standard' }; } transform(rawData) { const { measurements, tradeInputs, mode } = rawData; let lineItems = []; let summary = { total: 0, type: mode }; measurements.forEach(m => { }); if (mode === 'snow' && tradeInputs) { const snowInputs = { clearing: { enabled: true, loaderArea: parseFloat(tradeInputs.loaderArea) || 0, skidSteerArea: parseFloat(tradeInputs.skidSteerArea) || 0, shovelArea: parseFloat(tradeInputs.shovelArea) || 0 }, hauling: { enabled: tradeInputs.haulingEnabled || false, }, salting: { enabled: tradeInputs.saltingEnabled || false }, ...tradeInputs }; const result = calculateSnowServiceCost(snowInputs); lineItems.push({ description: 'Snow Clearing Service (Seasonal)', price: result.totals.seasonalPrice, details: result }); summary.total = result.totals.seasonalPrice; } return { lineItems, summary }; } async load(processedData, estimateRef) { console.log("[PricingBridge] Loading Estimate...", processedData); if (typeof window !== 'undefined' && window.firebaseServices) { const { updateDoc } = window.firebaseServices; await updateDoc(estimateRef, { lineItems: processedData.lineItems, estimatedTotal: processedData.summary.total, lastCalculated: new Date().toISOString() }); } else { console.log("[PricingBridge] Mock DB Update:", { lineItems: processedData.lineItems, total: processedData.summary.total }); } return { success: true }; } }