import { Subscribers } from './subscribers.js'; import { Actions } from './actions.js'; import { SmartMonitor } from './monitor.js'; import { TeamManager } from './team.js'; import { PlatformManager } from './platform.js'; import { PricingManager } from './pricing_manager.js'; import { EstimateFeed } from './estimate_feed.js'; import { TimeSheetFeed } from './timesheet_feed.js'; import { SafetyFeed } from './safety_feed.js'; class DeveloperConsole { constructor() { this.subscribers = new Subscribers(this); this.actions = new Actions(this); this.monitor = new SmartMonitor(); this.team = new TeamManager(this); this.platform = new PlatformManager(this); this.pricing = new PricingManager(this); this.feed = new EstimateFeed(this); this.timeSheet = new TimeSheetFeed(this); this.safety = new SafetyFeed(this); this.activeContext = 'dashboard'; } init() { console.log("[The Brain] Initializing..."); this.bindRail(); this.subscribers.init(); this.monitor.init(); this.team.init(); this.platform.init(); this.pricing.init(); this.feed.init(); this.timeSheet.init(); this.safety.init(); this.enforcePermissions(); this.switchContext('dashboard'); console.log("[The Brain] Ready."); } enforcePermissions() { const user = this.team.currentUser; if (!user) return; const businessBtn = document.querySelector('.rail-btn[data-context="business"]'); if (businessBtn) { if (user.permissions.view_financials) { businessBtn.style.display = 'flex'; } else { businessBtn.style.display = 'none'; if (this.activeContext === 'business') this.switchContext('dashboard'); } } } bindRail() { const btns = document.querySelectorAll('.rail-btn'); btns.forEach(btn => { btn.addEventListener('click', (e) => { const target = e.currentTarget.dataset.context; this.switchContext(target); }); }); } switchContext(contextId) { document.querySelectorAll('.rail-btn').forEach(b => b.classList.remove('active')); const btn = document.querySelector(`.rail-btn[data-context="${contextId}"]`); if (btn) btn.classList.add('active'); document.querySelectorAll('.workspace').forEach(ws => ws.classList.remove('active')); const ws = document.getElementById(`ws-${contextId}`); if (ws) ws.classList.add('active'); const titleMap = { 'dashboard': 'System Overview', 'tenants': 'Tenant Management', 'ops': 'Operations & Smart Monitor', 'platform': 'Platform Settings', 'business': 'Business Analytics', 'legal': 'Legal & Compliance' }; document.getElementById('context-title').innerText = titleMap[contextId] || 'Command Center'; this.activeContext = contextId; } onSubscriberSelected(subscriber) { this.actions.reset(); this.subscribers.renderModules(subscriber); } onModuleSelected(module, subscriber) { this.actions.renderConfiguration(module, subscriber); } } window.brain = new DeveloperConsole(); document.addEventListener('DOMContentLoaded', () => window.brain.init());