export class GeofenceManager { constructor() { this.zones = []; this.currentZoneId = null; this.listeners = { 'enter': [], 'exit': [], 'dwell': [] }; this.zoneEnterTime = null; } addZone(zoneConfig) { if (!window.google || !window.google.maps) { console.warn("Google Maps not ready for Geofence"); return; } const polygon = new google.maps.Polygon({ paths: zoneConfig.paths }); this.zones.push({ id: zoneConfig.id, name: zoneConfig.name, type: zoneConfig.type, polygon: polygon }); console.log(`Geofence added: ${zoneConfig.name} (${zoneConfig.type})`); } checkLocation(position) { if (!window.google || !window.google.maps) return; const latLng = new google.maps.LatLng(position.lat, position.lng); let foundZone = null; for (const zone of this.zones) { if (google.maps.geometry.poly.containsLocation(latLng, zone.polygon)) { foundZone = zone; break; } } if (foundZone) { if (this.currentZoneId !== foundZone.id) { this.handleEnter(foundZone); } else { } } else { if (this.currentZoneId !== null) { this.handleExit(this.getZoneById(this.currentZoneId)); } } } handleEnter(zone) { console.log(`Entered Zone: ${zone.name}`); this.currentZoneId = zone.id; this.zoneEnterTime = Date.now(); this._emit('enter', zone); window.dispatchEvent(new CustomEvent('geofence-enter', { detail: zone })); } handleExit(zone) { console.log(`Exited Zone: ${zone ? zone.name : 'Unknown'}`); const duration = this.zoneEnterTime ? (Date.now() - this.zoneEnterTime) : 0; this.currentZoneId = null; this.zoneEnterTime = null; this._emit('exit', { zone, duration }); window.dispatchEvent(new CustomEvent('geofence-exit', { detail: { zone, duration } })); } getZoneById(id) { return this.zones.find(z => z.id === id); } getCurrentZone() { if (!this.currentZoneId) return null; return this.getZoneById(this.currentZoneId); } on(event, callback) { if (this.listeners[event]) this.listeners[event].push(callback); } _emit(event, data) { if (this.listeners[event]) { this.listeners[event].forEach(cb => cb(data)); } } }