export class DashcamWidget { constructor(config = {}) { this.containerId = config.containerId || 'dashcam-container'; this.onStatusChange = config.onStatusChange || (() => { }); this.mediaRecorder = null; this.recordedChunks = []; this.isRecording = false; this.stream = null; this.bufferLimit = Math.ceil((2 * 60 * 1000) / 5000); } render() { const container = document.getElementById(this.containerId); if (!container) { console.error(`Dashcam Widget: Container #${this.containerId} not found`); return; } container.innerHTML = ` <div class="relative w-full h-full bg-black rounded-lg overflow-hidden border-2 border-gray-700 shadow-xl group"> <!-- Video Feed --> <video id="${this.containerId}-video" class="w-full h-full object-cover transition-all duration-300 transform scale-100 group-hover:scale-105" autoplay playsinline muted></video> <!-- Overlay: Status --> <div class="absolute top-2 left-2 flex items-center gap-2"> <div id="${this.containerId}-badge" class="px-2 py-1 rounded bg-gray-800/80 text-xs font-bold text-gray-400">STANDBY</div> <div class="text-[10px] text-gray-500 font-mono">CAM-01 (FRONT)</div> </div> <!-- Overlay: Camera Switcher (Restored Feature) --> <button id="${this.containerId}-btn-switch" class="absolute top-2 right-2 p-2 bg-gray-800/60 rounded-full text-white hover:bg-gray-700 backdrop-blur-sm transition-all"> <svg class="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15"></path></svg> </button> <!-- Overlay: Actions --> <div class="absolute bottom-2 left-0 right-0 flex justify-center gap-2 px-2"> <button id="${this.containerId}-btn-incident" class="hidden bg-red-600 hover:bg-red-700 text-white text-xs font-bold py-2 px-6 rounded-full shadow-lg transition-all animate-bounce-in border-2 border-red-400"> SAVE INCIDENT </button> </div> <!-- Overlay: Error/Message --> <div id="${this.containerId}-toast" class="hidden absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 bg-black/90 text-white text-sm px-6 py-3 rounded-xl text-center border border-gray-600 shadow-2xl z-50"> </div> </div> `; this.videoEl = document.getElementById(`${this.containerId}-video`); this.statusBadge = document.getElementById(`${this.containerId}-badge`); this.incidentBtn = document.getElementById(`${this.containerId}-btn-incident`); this.switchBtn = document.getElementById(`${this.containerId}-btn-switch`); this.toastEl = document.getElementById(`${this.containerId}-toast`); this.incidentBtn.addEventListener('click', () => this.saveIncident()); this.switchBtn.addEventListener('click', () => this.switchCamera()); } async switchCamera() { this.showToast("Switching to Rear Camera..."); await new Promise(r => setTimeout(r, 800)); this.showToast("Rear Camera Active"); } async start() { if (this.isRecording) return; if (!this.videoEl) this.render(); try { if (navigator.mediaDevices.getUserMedia.toString().includes('mock')) { console.log("Dashcam: Detecting Mock Camera..."); } this.stream = await navigator.mediaDevices.getUserMedia({ video: true, audio: true }); this.videoEl.srcObject = this.stream; const mimeType = MediaRecorder.isTypeSupported("video/webm; codecs=vp9") ? "video/webm; codecs=vp9" : "video/mp4"; this.mediaRecorder = new MediaRecorder(this.stream, { mimeType }); this.recordedChunks = []; this.mediaRecorder.ondataavailable = (e) => { if (e.data.size > 0) { this.recordedChunks.push(e.data); if (this.recordedChunks.length > this.bufferLimit) this.recordedChunks.shift(); } }; this.mediaRecorder.start(5000); this.isRecording = true; this.updateUI('recording'); this.onStatusChange('recording'); } catch (err) { console.error("Dashcam Start Error:", err); this.showToast("Camera Error: " + err.message); this.onStatusChange('error', err.message); } } stop() { if (this.mediaRecorder && this.isRecording) { this.mediaRecorder.stop(); this.isRecording = false; } if (this.stream) { this.stream.getTracks().forEach(t => t.stop()); this.stream = null; } this.updateUI('stopped'); this.onStatusChange('stopped'); } async saveIncident(jobId = 'adhoc') { if (this.recordedChunks.length === 0) return this.showToast("Buffering... wait 5s"); this.updateUI('uploading'); try { await new Promise(r => setTimeout(r, 1500)); if (window.firebaseServices && window.firebaseServices.storage) { } this.updateUI('saved'); this.onStatusChange('saved'); setTimeout(() => this.updateUI('recording'), 3000); } catch (e) { this.showToast("Save Failed"); this.updateUI('recording'); } } updateUI(state) { if (!this.statusBadge) return; if (state === 'recording') { this.statusBadge.textContent = "REC • BUFFERING"; this.statusBadge.className = "px-2 py-1 rounded bg-red-600 text-white text-xs font-bold animate-pulse"; this.incidentBtn.classList.remove('hidden'); this.incidentBtn.textContent = "SAVE INCIDENT"; this.incidentBtn.disabled = false; } else if (state === 'stopped') { this.statusBadge.textContent = "STANDBY"; this.statusBadge.className = "px-2 py-1 rounded bg-gray-800/80 text-xs font-bold text-gray-400"; this.incidentBtn.classList.add('hidden'); } else if (state === 'uploading') { this.incidentBtn.textContent = "UPLOADING..."; this.incidentBtn.disabled = true; } else if (state === 'saved') { this.incidentBtn.textContent = "SAVED!"; this.incidentBtn.className = "bg-green-600 text-white text-xs font-bold py-2 px-4 rounded-full shadow-lg"; } } showToast(msg) { if (!this.toastEl) return; this.toastEl.textContent = msg; this.toastEl.classList.remove('hidden'); setTimeout(() => this.toastEl.classList.add('hidden'), 3000); } }