export const SnowOperations = { daisyChainSite: function (currentLog, nextJob, user) { const now = new Date().toISOString(); const finishedLog = { ...currentLog, endTime: now, status: 'Completed', durationMinutes: this.calculateDurationMinutes(currentLog.startTime, now) }; const newLog = { id: `snow_log_${Date.now()}`, shiftId: currentLog.shiftId, jobId: nextJob.id, propertyId: nextJob.locationId || 'unknown_loc', serviceType: 'Clearing', billingType: nextJob.snowContract?.billingType || 'PerPush', startTime: now, endTime: null, status: 'Active', photos: { before: [], after: [], damage: [] }, materials: {}, weather: {} }; return { finishedLog, newLog }; }, startSnowShift: function (user, assetId) { return { id: `shift_${user.uid}_${Date.now()}`, userId: user.uid, userName: user.displayName || user.email, assetId: assetId, startTime: new Date().toISOString(), endTime: null, status: 'Active', totalHours: 0 }; }, endSnowShift: function (shift) { const now = new Date().toISOString(); return { ...shift, endTime: now, status: 'Completed', totalHours: this.calculateDurationHours(shift.startTime, now) }; }, generateInvoiceItems: function (snowLogs, contracts) { const invoiceItems = []; snowLogs.forEach(log => { const contract = contracts[log.jobId]?.snowContract; if (!contract) { console.warn(`No contract found for job ${log.jobId}`); return; } let billableAmount = 0; let description = ''; let quantity = 0; let rate = 0; if (contract.billingType === 'Hourly') { const hours = log.durationMinutes / 60; rate = contract.rates?.clearing || 0; billableAmount = hours * rate; description = `Snow Clearing (Hourly): ${hours.toFixed(2)} hrs`; quantity = hours; } else if (contract.billingType === 'PerPush') { rate = contract.rates?.perPush || 0; billableAmount = rate; description = `Snow Clearing (Per Push)`; quantity = 1; if (log.serviceType === 'Salting' || log.materials?.saltLbs > 0) { const saltRate = contract.rates?.salting || 0; billableAmount += saltRate; description += ` + Salting`; } } else if (contract.billingType === 'Seasonal') { billableAmount = 0; description = `Snow Clearing (Seasonal Contract)`; quantity = 1; rate = 0; } invoiceItems.push({ jobId: log.jobId, logId: log.id, description, quantity, rate, total: billableAmount, date: log.startTime }); }); return invoiceItems; }, calculateDurationMinutes: function (startStr, endStr) { const start = new Date(startStr); const end = new Date(endStr); return (end - start) / 60000; }, calculateDurationHours: function (startStr, endStr) { return this.calculateDurationMinutes(startStr, endStr) / 60; } };