const TIER_RANK = { 'LEVEL_1': 1, 'LEVEL_2': 2, 'LEVEL_3': 3 }; export const Governance = { getEffectiveTier(subscriptionTier, userRoleLimit) { if (!TIER_RANK[subscriptionTier]) subscriptionTier = 'LEVEL_1'; if (!TIER_RANK[userRoleLimit]) userRoleLimit = 'LEVEL_1'; const subRank = TIER_RANK[subscriptionTier]; const userRank = TIER_RANK[userRoleLimit]; return subRank < userRank ? subscriptionTier : userRoleLimit; }, isTierAtLeast(currentTier, requiredTier) { const cur = TIER_RANK[currentTier] || 0; const req = TIER_RANK[requiredTier] || 0; return cur >= req; } };