export const NativeBridge = { isNative: () => { return window.Capacitor && window.Capacitor.isNativePlatform(); }, getPlatform: () => { if (!window.Capacitor) return 'web'; return window.Capacitor.getPlatform(); }, requestPermission: async (type) => { if (!NativeBridge.isNative()) { console.log(`[Web] Requesting permission for ${type} (Simulated)`); return 'granted'; } return 'granted'; } }; document.addEventListener('DOMContentLoaded', () => { const platform = NativeBridge.getPlatform(); document.body.classList.add(`platform-${platform}`); if (NativeBridge.isNative()) { document.body.classList.add('is-native'); } });