export const ThemeEngine = { currentTheme: { primary: '#2563eb', secondary: '#475569', bg: '#f8fafc', surface: '#ffffff', text: '#0f172a', radius: '0.5rem', font: 'Inter, sans-serif' }, applyTheme: (theme = {}) => { const t = { ...ThemeEngine.currentTheme, ...theme }; ThemeEngine.currentTheme = t; const root = document.documentElement; root.style.setProperty('--primary', t.primary); root.style.setProperty('--secondary', t.secondary); root.style.setProperty('--bg-color', t.bg); root.style.setProperty('--surface-color', t.surface); root.style.setProperty('--text-color', t.text); root.style.setProperty('--radius', t.radius); root.style.setProperty('--font-family', t.font); ThemeEngine.generatePalette(t.primary, 'primary'); console.log("Theme Applied:", t); }, generatePalette: (hex, name) => { const root = document.documentElement; root.style.setProperty(`--${name}-50`, ThemeEngine.adjustBrightness(hex, 0.9)); root.style.setProperty(`--${name}-100`, ThemeEngine.adjustBrightness(hex, 0.8)); root.style.setProperty(`--${name}-500`, hex); root.style.setProperty(`--${name}-600`, ThemeEngine.adjustBrightness(hex, -0.1)); root.style.setProperty(`--${name}-900`, ThemeEngine.adjustBrightness(hex, -0.4)); }, adjustBrightness: (hex, percent) => { return hex; }, suggestThemes: (seedColor) => { return [ { name: 'Professional', primary: seedColor, bg: '#f8fafc', radius: '0.3rem' }, { name: 'Modern', primary: seedColor, bg: '#ffffff', radius: '1rem' }, { name: 'Dark Mode', primary: seedColor, bg: '#0f172a', surface: '#1e293b', text: '#f8fafc', radius: '0.5rem' } ]; } }; document.addEventListener('DOMContentLoaded', () => { ThemeEngine.applyTheme(); });